mapper.select:
SELECT MEMBER_ID, PASSWORD, ROLE 
FROM MEMBER
WHERE MEMBER_ID=#{member_id} AND PASSWORD=#{password}

SELECT MEMBER_ID 
FROM MEMBER_INFOS
WHERE NAME = #{name} AND PHONE = #{phone}

mapper.update:
UPDATE MEMBER
SET PASSWORD = #{password}
WHERE MEMBER_ID = (SELECT MEMBER_ID FROM MEMBER_INFOS WHERE PHONE = #{phone})

UPDATE MEMBER_INFO 
SET NAME = #{name}, 
	EMAIL = #{email}, 
	PHONE = #{phone}, 
	BIRTH = #{birth} 
WHERE MEMBER_ID = #{member_id};

UPDATE ADDRESS 
SET ZIPCODE = #{zipcode}, 
	ADDRESS = #{address}, 
	ADDRESS2 = #{address2} 
WHERE MEMBER_ID = #{member_id};
