mapper.select:
SELECT MEMBER_ID, PASSWORD, ROLE, LEVEL 
FROM MEMBERS
WHERE MEMBER_ID=#{member_id} AND PASSWORD=#{password}

SELECT MEMBER_ID
FROM MEMBER_INFOS
WHERE NAME = #{name} AND EMAIL = #{email}

mapper.update:
UPDATE MEMBERS
SET PASSWORD = #{password}
WHERE MEMBER_ID = (SELECT MEMBER_ID FROM MEMBER_INFOS WHERE EMAIL = #{email})

mapper.delete:
UPDATE MEMBERS
SET ROLE = 'ARCHIVED'
WHERE MEMBER_ID = #{value}

mapper.select:
SELECT ZIPCODE, ADDRESS, ADDRESS2
FROM ADDRESSES
WHERE MEMBER_ID = #{value}

mapper.update:
UPDATE ADDRESS SET
	ZIPCODE  = #{zipcode},
	ADDRESS  = #{address},
	ADDRESS2 = #{address2}
WHERE MEMBER_ID = #{member_id}

UPDATE MEMBER_INFOS SET
 	NAME  = #{name},
	EMAIL = #{email},
	PHONE = #{phone},
	BIRTH = #{birth}
WHERE MEMBER_ID = #{member_id}
